import React from 'react';

export const PageCenter = (props) => {
  return (
    <div style={props.threadOpen ? styles.backgroundOpen: styles.backgroundClosed}>
      {props.threadOpen}
    </div>
  );
};

const styles = {
  backgroundClosed: {
    background: 'black',
    height: '100%',
    width: '100%-400px',
    minWidth: 235,
    position: 'absolute',
    left: 220,
    right: 0,
    top: 0,
    display: 'block',
    overflow: 'hidden',
    zIndex: 9,
  },
  backgroundOpen: {
    background: 'black',
    height: '100%',
    width: '100%-400px',
    minWidth: 235,
    position: 'absolute',
    left: 220,
    right: 366,
    top: 0,
    display: 'block',
    overflow: 'hidden',
    zIndex: 9,
  },
};
